class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profile: {
        photoURL: '',
        nickname: '',
        bio: '',
        dream: '',
        age: '',
        gender: '',
      },
      exp: 0,
      isUpdating: false,
      isSelected: true,
      bgIndex: 0,
      currentListIndex: 0,
      lists: Array(5).fill({ selectedId: null }),
      currentClothesSet: 'm0',
      currentClothesIndex: 0,
      clothesIndices: {
        m0: 0,
        m1: 0,
        m2: 0,
        m3: 0,
        m4: 0
      }
    };
  }


  componentDidMount() {
    const user = auth.currentUser;
    if (user) {
      const docRef = doc(db, "profiles", user.uid);
      getDoc(docRef).then((docSnap) => {
        const data = docSnap.data();
        this.setState({ profile: data, exp: data.memorized || 0 });
      });
    }
  }

  saveProfile = () => {
    if (this.state.isUpdating) return;  // Exit if an update is in progress

    const user = auth.currentUser;
    if (user) {
      this.setState({ isUpdating: true });

      const docRef = doc(db, "profiles", user.uid);
      setDoc(docRef, this.state.profile, { merge: true })
        .then(() => {
          Alert.alert("Profile updated successfully!");
          this.setState({ isUpdating: false });
        })
        .catch((error) => {
          Alert.alert("Error updating profile: ", error.message);
          this.setState({ isUpdating: false });
        });
    }
  }




  getLevel = (exp) => {
    if (exp === 0) return 'Beginner';
    return `Level ${Math.log10(exp).toFixed(0)}`;
  }

  uploadPhotoFromURL = async () => {
    const user = auth.currentUser;
    const { photoURL } = this.state.profile;

    if (user && photoURL) {
      const response = await fetch(photoURL);
      const blob = await response.blob();
      const storageRef = ref(storage, `profile_photos/${user.uid}`);
      const snapshot = await uploadBytes(storageRef, blob);
      const uploadedURL = await getDownloadURL(snapshot.ref);
      this.setState(prevState => ({ profile: { ...prevState.profile, photoURL: uploadedURL } }));
    }
  }

  handleInputChange = (key, value) => {
    this.setState(prevState => ({ profile: { ...prevState.profile, [key]: value } }), () => {
      this.saveProfile();
    });
  }

  changeBackground = (direction) => {
    let { bgIndex } = this.state;
    const bgCount = 13;  // Hard-coded for now

    if (direction === 'next') {
      bgIndex = (bgIndex + 1) % bgCount;
    } else {
      bgIndex = (bgIndex + bgCount - 1) % bgCount;
    }

    this.setState({ bgIndex });
  }

  dressRandom = () => {
    const lists = this.state.lists.map(list => {
      const keys = Object.keys(list);
      const keyIndex = Math.floor(Math.random() * keys.length);
      const id = keys[keyIndex];

      return {
        ...list,
        selectedId: id
      };
    });

    this.setState({ lists });
  }

  undress = () => {
    const lists = this.state.lists.map(list => ({
      ...list,
      selectedId: null
    }));

    this.setState({ lists });
  }

  changeClothes = (clothesId) => {
    this.setState({
      currentClothesSet: clothesId,
      currentClothesIndex: 0
    }, this.saveClothesStatus);
  }

  nextClothesImage = () => {
    this.setState(prevState => {
      const nextIndex = (prevState.currentClothesIndex + 1) % (clothesImages[prevState.currentClothesSet].default.length);
      return { currentClothesIndex: nextIndex };
    });
  }


  saveClothesStatus = () => {
    const user = auth.currentUser;
    if (user) {
      const docRef = doc(db, "profiles", user.uid);
      setDoc(docRef, { clothesStatus: this.state.currentClothesSet }, { merge: true })
        .then(() => {
          console.log("Clothes status updated successfully!");
        })
        .catch((error) => {
          console.error("Error updating clothes status: ", error);
        });
    }
  }

  changeClothesPiece = (clothesSet) => {
    this.setState(prevState => {
      const currentSetIndex = prevState.clothesIndices[clothesSet];
      const nextIndex = (currentSetIndex + 1) % (clothesImages[clothesSet].default.length);
      return {
        clothesIndices: {
          ...prevState.clothesIndices,
          [clothesSet]: nextIndex
        }
      };
    }, this.saveClothesStatus);
  }

  saveNFTDataToFirebase = () => {
    const user = auth.currentUser;
    if (user) {
      const nftData = {
        "CONTRACT_ADDRESS": "0x0089206500CFba2Be87b2c51cE39Efa72910C2BD",
        "SCAN_LINK": "https://polygonscan.com/token/0x0089206500CFba2Be87b2c51cE39Efa72910C2BD",
        "NETWORK": {
          "NAME": "Polygon",
          "SYMBOL": "Matic",
          "ID": 137
        },
        "NFT_NAME": `${this.state.profile.nickname}_${Object.keys(this.state.clothesIndices).join('_')}`,
        "SYMBOL": "TSNFT",
        "MAX_SUPPLY": 1,
        "WEI_COST": 0,
        "DISPLAY_COST": 0,
        "GAS_LIMIT": 200000,
        "MARKETPLACE": "Opensea",
        "MARKETPLACE_LINK": "https://opensea.io/collection/fundamental-fundy",
        "SHOW_BACKGROUND": true
      };

      const docRef = doc(db, "profiles", "NFT");
      setDoc(docRef, nftData)
        .then(() => {
          Alert.alert("NFT Data saved successfully!");
        })
        .catch((error) => {
          Alert.alert("Error saving NFT Data: ", error.message);
        });
    }
  }

  renderProfileForm = () => (
    <SafeAreaView style={styles.container}>
      <View style={styles.container}>
        <Text style={styles.inputhelp}>
          Bio
        </Text>
        <TextInput
          style={styles.input}
          value={this.state.profile.bio}
          onChangeText={(text) => {
            this.setState(prevState => ({ profile: { ...prevState.profile, bio: text } }));
          }}
          onBlur={this.saveProfile}
          placeholder="Your Text Here"
        />
        <Text style={styles.inputhelp}>
          Dream Job
        </Text>
        <TextInput
          style={styles.input}
          value={this.state.profile.dream}
          onChangeText={(text) => {
            this.setState(prevState => ({ profile: { ...prevState.profile, dream: text } }));
          }}
          onBlur={this.saveProfile}
          placeholder="Your Text Here"
        />
        <Text style={styles.inputhelp}>
          Age
        </Text>
        <TextInput
          style={styles.input}
          value={this.state.profile.age}
          onChangeText={(text) => {
            this.setState(prevState => ({ profile: { ...prevState.profile, age: text } }));
          }}
          onBlur={this.saveProfile}
          placeholder="Your Text Here"
        />
        <Text style={styles.inputhelp}>
          Gender
        </Text>
        <TextInput
          style={styles.input}
          value={this.state.profile.gender}
          onChangeText={(text) => {
            this.setState(prevState => ({ profile: { ...prevState.profile, gender: text } }));
          }}
          onBlur={this.saveProfile}
          placeholder="Your Text Here"
        />
      </View>
    </SafeAreaView>
  );

  render() {
    const progress = this.state.exp / TOTAL_WORDS;
    const backgroundImage = `img/bg/t${this.state.bgIndex}.jpg`;  // Sample path
    const { currentClothesSet, currentClothesIndex } = this.state;
    return (
      <ScrollView style={styles.scroll}>
        <View style={styles.container}>
          <Header2
            text="Profile"
            leftButton={<LadderIcon height={30} width={30} />}
            rightButton={null}
          />
          {/* Clothes buttons */}
          <View style={{ flexDirection: 'row', marginVertical: 10 }}>
            <TouchableOpacity onPress={this.changeBackground} style={{ marginHorizontal: 10 }}>
              <Text>Bg</Text>
            </TouchableOpacity>
            {['m0', 'm1', 'm2', 'm3', 'm4'].map((clothesId) => (
              <TouchableOpacity
                key={clothesId}
                style={{ marginHorizontal: 10 }}
                onPress={() => this.changeClothesPiece(clothesId)}
              >
                <Text>{clothesId.toUpperCase()}</Text>
              </TouchableOpacity>
            ))}
          </View>
          <TextInput
            style={styles.inputsmall}
            value={this.state.profile.nickname}
            onChangeText={(text) => {
              this.setState(prevState => ({ profile: { ...prevState.profile, nickname: text } }));
            }}
            onBlur={this.saveProfile}
            placeholder="Nickname"
          />
          <Text style={styles.levelText}>{this.getLevel(this.state.exp)}</Text>
          <ProgressBar progress={progress} color='#1404bc' style={{ height: 30, width: Dimensions.get('window').width, margin: 10 }} />

          <ImageBackground
            source={bgImages[this.state.bgIndex]}
            style={styles.coverImage}
          >
            {Object.keys(this.state.clothesIndices).map(clothesSet => {
              const currentIndex = this.state.clothesIndices[clothesSet];
              return <Image key={clothesSet} source={clothesImages[clothesSet]?.default[currentIndex]} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }} resizeMode="cover" />;
            })}
          </ImageBackground>
          {this.renderProfileForm()}
        </View>
        <TouchableOpacity onPress={this.saveNFTDataToFirebase} style={{ padding: 10, backgroundColor: "#2196F3", margin: 10 }}>
          <Text style={{ color: "white", textAlign: "center" }}>Request Minting</Text>
        </TouchableOpacity>
      </ScrollView>
    );
  }
}

Profile.propTypes = {
  img: PropTypes.string,
  navigation: PropTypes.object.isRequired,
};

export default Profile;